mapper.select:
select userId, userName, userSex, userPhone, userEmail
from tb_user
where userId = #{userId}

mapper.update:
update tb_user set userName = #{userName}, userSex = #{userSex}

    ,userPhone = #{userPhone}


    ,userEmail = #{userEmail}

where userId = #{userId}

mapper.select:
select adminId, adminPassword
from tb_admin
where adminId = #{adminId}
